# else if statement (elif)

nama = input("Masukkan nama Anda! ")

if nama == "ucup": # kondisi 1
    print("haloooo cuppppppp") # kondisi true 1
elif nama == "otong" : # kondisi 2 
    print("tong bayar tong") # kondisi true 2
elif nama == "kokong" : # kondisi 3
    print("kong bayar tong") # kondisi true 3
else:
    print("luu  olang siapa?") # aksi false

print("ini adalah akhir dari program")